import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';
import axios from 'axios';
import "../navbar/navBar.css";

function AddDoctor(props) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [category, setCategory] = useState(1); // Assuming category is an integer
  const [availableAppointments, setAvailableAppointments] = useState([
    { id: 1, startDay: '', endDay: '', startTime: '', endTime: '' }
  ]);
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const defa = () => {
    setUsername('');
    setPassword('');
    setDateOfBirth('');
    setEmail('');
    setPhone('');
    setCategory(1); // Resetting category to default value
    setAvailableAppointments([
      { id: 1, startDay: '', endDay: '', startTime: '', endTime: '' }
    ]);
    setError('');
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const newUser = {
      username: username,
      password: password,
      dateofbirth: dateOfBirth,
      email: email,
      phone: phone,
      category: category,
      availableAppointments: availableAppointments
    };

    try {
      const response = await axios.post('http://localhost:8800/api/auth/register', newUser);
      console.log(response.data);
      props.onHide();
      defa();
    } catch (error) {
      if (error.response && error.response.status === 400 && error.response.data === 'Email already exists') {
        setError('Email is already registered. Please use a different email.');
      } else {
        setError('Registration failed. Please try again.');
      }
    }
  };

  const toggleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleAddAppointmentSlot = () => {
    const newSlot = { id: availableAppointments.length + 1, startDay: '', endDay: '', startTime: '', endTime: '' };
    setAvailableAppointments([...availableAppointments, newSlot]);
  };

  const handleAppointmentInputChange = (id, event) => {
    const updatedAppointments = availableAppointments.map((slot) => {
      if (slot.id === id) {
        return { ...slot, [event.target.name]: event.target.value };
      }
      return slot;
    });
    setAvailableAppointments(updatedAppointments);
  };

  return (
    <div className='custom-modal'>
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        scrollable
        className="custom-modal-content"
        dialogClassName="custom-modal-dialog"
      >
        <Modal.Header closeButton className="custom-modal-header">
          <Modal.Title id="contained-modal-title-vcenter" className="text-center w-100">
            Register Doctor
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="custom-modal-body">
          <Form onSubmit={handleSubmit}>
            {error && <Alert variant="danger">{error}</Alert>}
            <Form.Group className="mb-3" controlId="username">
              <Form.Label>Username</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="password">
              <Form.Label>Password</Form.Label>
              <div className="password-field">
                <Form.Control
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <Button variant="outline-secondary" onClick={toggleShowPassword}>
                  {showPassword ? "Hide" : "Show"}
                </Button>
              </div>
            </Form.Group>

            <Form.Group className="mb-3" controlId="dateOfBirth">
              <Form.Label>Date of Birth</Form.Label>
              <Form.Control
                type="date"
                value={dateOfBirth}
                onChange={(e) => setDateOfBirth(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="email">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="phone">
              <Form.Label>Phone Number</Form.Label>
              <Form.Control
                type="tel"
                placeholder="Enter phone number"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="category">
              <Form.Label>Category</Form.Label>
              <Form.Control
                as="select"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                required
              >
                <option value="1">Category 1</option>
                <option value="2">Category 2</option>
                <option value="3">Category 3</option>
              </Form.Control>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Available Appointments</Form.Label>
              {availableAppointments.map((slot) => (
                <div key={slot.id}>
                  <Form.Group className="mb-3">
                    <Form.Label>Start Day</Form.Label>
                    <Form.Select
                      value={slot.startDay}
                      onChange={(e) => handleAppointmentInputChange(slot.id, e)}
                      name={`availableAppointments.${slot.id}.startDay`}
                      required
                    >
                      <option value="">Select start day...</option>
                      <option value="Monday">Monday</option>
                      <option value="Tuesday">Tuesday</option>
                      <option value="Wednesday">Wednesday</option>
                      <option value="Thursday">Thursday</option>
                      <option value="Friday">Friday</option>
                      <option value="Saturday">Saturday</option>
                      <option value="Sunday">Sunday</option>
                    </Form.Select>
                  </Form.Group>
                  <Form.Group className="mb-3">
                    <Form.Label>End Day</Form.Label>
                    <Form.Select
                      value={slot.endDay}
                      onChange={(e) => handleAppointmentInputChange(slot.id, e)}
                      name={`availableAppointments.${slot.id}.endDay`}
                      required
                    >
                      <option value="">Select end day...</option>
                      <option value="Monday">Monday</option>
                      <option value="Tuesday">Tuesday</option>
                      <option value="Wednesday">Wednesday</option>
                      <option value="Thursday">Thursday</option>
                      <option value="Friday">Friday</option>
                      <option value="Saturday">Saturday</option>
                      <option value="Sunday">Sunday</option>
                    </Form.Select>
                  </Form.Group>
                  <Form.Group className="mb-3">
                    <Form.Label>Start Time</Form.Label>
                    <Form.Select
                      value={slot.startTime}
                      onChange={(e) => handleAppointmentInputChange(slot.id, e)}
                      name={`availableAppointments.${slot.id}.startTime`}
                      required
                    >
                      <option value="">Select start time...</option>
                      <option value="09:00 AM">09:00 AM</option>
                      <option value="09:30 AM">09:30 AM</option>
                      <option value="10:00 AM">10:00 AM</option>
                      <option value="10:30 AM">10:30 AM</option>
                      <option value="11:00 AM">11:00 AM</option>
                      <option value="11:30 AM">11:30 AM</option>
                      <option value="12:00 PM">12:00 PM</option>
                      <option value="12:30 PM">12:30 PM</option>
                      <option value="01:00 PM">01:00 PM</option>
                      <option value="01:30 PM">01:30 PM</option>
                      <option value="02:00 PM">02:00 PM</option>
                      <option value="02:30 PM">02:30 PM</option>
                      <option value="03:00 PM">03:00 PM</option>
                      <option value="03:30 PM">03:30 PM</option>
                      <option value="04:00 PM">04:00 PM</option>
                      <option value="04:30 PM">04:30 PM</option>
                      <option value="05:00 PM">05:00 PM</option>
                    </Form.Select>
                  </Form.Group>
                  <Form.Group className="mb-3">
                    <Form.Label>End Time</Form.Label>
                    <Form.Select
                      value={slot.endTime}
                      onChange={(e) => handleAppointmentInputChange(slot.id, e)}
                      name={`availableAppointments.${slot.id}.endTime`}
                      required
                    >
                      <option value="">Select end time...</option>
                      <option value="09:00 AM">09:00 AM</option>
                      <option value="09:30AM">09:30 AM</option>
                      <option value="10:00 AM">10:00 AM</option>
                      <option value="10:30 AM">10:30 AM</option>
                      <option value="11:00 AM">11:00 AM</option>
                      <option value="11:30 AM">11:30 AM</option>
                      <option value="12:00 PM">12:00 PM</option>
                      <option value="12:30 PM">12:30 PM</option>
                      <option value="01:00 PM">01:00 PM</option>
                      <option value="01:30 PM">01:30 PM</option>
                      <option value="02:00 PM">02:00 PM</option>
                      <option value="02:30 PM">02:30 PM</option>
                      <option value="03:00 PM">03:00 PM</option>
                      <option value="03:30 PM">03:30 PM</option>
                      <option value="04:00 PM">04:00 PM</option>
                      <option value="04:30 PM">04:30 PM</option>
                      <option value="05:00 PM">05:00 PM</option>
                    </Form.Select>
                  </Form.Group>
                </div>
              ))}
              <Button variant="success" onClick={handleAddAppointmentSlot}>
                Add Appointment Slot
              </Button>
            </Form.Group>

            <div className="text-center">
              <Button variant="success" type="submit">
                Register
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default AddDoctor;
