import React, { useState, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

function AppointmentForm({ doctorId, onHide }) {
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [doctorInfo, setDoctorInfo] = useState([]);
  const [availableDays, setAvailableDays] = useState([]);
  const [selectedDayInfo, setSelectedDayInfo] = useState(null);
  const [tokenNumber, setTokenNumber] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8800/api/doctor/info/${doctorId}`)
      .then(response => {
        setDoctorInfo(response.data);
        generateAvailableDays(response.data.availableAppointments);
      })
      .catch(error => console.error(error));
  }, [doctorId]);

  const generateAvailableDays = (availableAppointments) => {
    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const availableDays = [];

    for (let i = 0; i < 14; i++) {
      const currentDay = new Date();
      currentDay.setDate(currentDay.getDate() + i);
      const dayName = daysOfWeek[currentDay.getDay()];

      const dayInfo = availableAppointments.find(appointment => appointment.Day === dayName && appointment.available);
      if (dayInfo) {
        availableDays.push({
          date: currentDay.toISOString().split('T')[0],
          ...dayInfo
        });
      }
    }

    setAvailableDays(availableDays);
  };

  const handleDateChange = async (selectedDate) => {
    setDate(selectedDate);
    const dayInfo = availableDays.find(day => day.date === selectedDate);
    setSelectedDayInfo(dayInfo);
    if (dayInfo) {
      setTime(`${dayInfo.startTime} - ${dayInfo.endTime}`);
      
      // Fetch existing appointments to determine the token number
      const response = await axios.get(`http://localhost:8800/api/appointmentsbydate?doctorid=${doctorId}&date=${selectedDate}`);
      const existingAppointments = response.data;
      const tokenNumber = existingAppointments.length + 1;

      setTokenNumber(tokenNumber);
    } else {
      setTime('');
      setTokenNumber(null);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const userId = storedUser._id;
    if (!userId) {
      console.error("User ID not found in local storage");
      return;
    }

    const newAppointment = {
      userid: userId,
      doctorid: doctorId,
      date: date,
      day: new Date(date).toLocaleString('en-US', { weekday: 'long' })
    };

    try {
      const response = await axios.post('http://localhost:8800/api/appointments', newAppointment);
      console.log(response.data);
      onHide();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="date">
        <Form.Label>Select Date</Form.Label>
        <Form.Control
          as="select"
          value={date}
          onChange={(e) => handleDateChange(e.target.value)}
          required
        >
          <option value="">Select a date</option>
          {availableDays.map((day, index) => (
            <option key={index} value={day.date}>
              {new Date(day.date).toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}
            </option>
          ))}
        </Form.Control>
      </Form.Group>
      {selectedDayInfo && (
        <>
          <Form.Group className="mb-3" controlId="time">
            <Form.Label>Available Time</Form.Label>
            <Form.Control
              type="text"
              value={time}
              readOnly
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="tokens">
            <Form.Label>Token Number</Form.Label>
            <Form.Control
              type="text"
              value={tokenNumber}
              readOnly
            />
          </Form.Group>
        </>
      )}
      <div className="text-center">
        <Button variant="outline-success" type="submit">Book Appointment</Button>
      </div>
    </Form>
  );
}

export default AppointmentForm;
