import React, { useState, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

function AppointmentForm({ doctorId, onHide }) {
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [doctorinfo,setDoctorinfo] = useState([]);
  const [startday,setStartday] =useState();
  const [endday,setEndday] =useState();
  useEffect(() => {
    // Fetch available appointments for the selected doctor
    axios.get(`http://localhost:8800/api/doctor/info/${doctorId}`)
      .then(response => setDoctorinfo(response.data))
      .catch(error => console.error(error));
  }, [doctorId]);

      const availabledates= doctorinfo.availableAppointments
      setStartday(availabledates['startDay'])
      setEndday(availabledates['endDay'])
      setTime(availabledates['startTime']+'-'+availabledates['endTime'])

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const userId = storedUser._id
    if (!userId) {
      console.error("User ID not found in local storage");
      return;
    }

    const newAppointment = {
      userid: userId,
      doctorid: doctorId,
      date: date,
      day: new Date(date).toLocaleString('en-US', { weekday: 'long' })
    };

    try {
      const response = await axios.post('http://localhost:8800/api/appointments', newAppointment);
      console.log(response.data);
      onHide();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="date">
        <Form.Label>Date</Form.Label>
        <Form.Control
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
      </Form.Group> 
      <Form.Group className="mb-3" controlId="time">
        <Form.Label>Available Time</Form.Label>
        <Form.Control
          type="string"
          value={time}
          readOnly
        />
      </Form.Group> 
      <div className="text-center">
        <Button variant="outline-success" type="submit">Book Appointment</Button>
      </div>
    </Form>
  );
}

export default AppointmentForm;
