import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Nav, Card } from 'react-bootstrap';
import axios from 'axios';
import "./Profile.css";

const UserInformation = () => {
  const [view, setView] = useState('profile'); // Manage the view state
  const [user, setUser] = useState({
    username: '',
    lastname: '',
    dateOfBirth: null,
    gender: '',
    email: '',
    phone: '',
    address: '',
    bloodType: ''
  });
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    // Fetch user data from local storage or API
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUser({
        ...storedUser,
        dateOfBirth: storedUser.dateOfBirth ? new Date(storedUser.dateOfBirth).toISOString().split('T')[0] : ''
      });

      // Fetch appointments
      fetchAppointments(storedUser._id);
    }
  }, []);

  const fetchAppointments = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:8800/api/appointments/${userId}`);
      setAppointments(response.data);
    } catch (error) {
      console.error('Error fetching appointments', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleDateChange = (e) => {
    setUser({ ...user, dateOfBirth: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., save data to local storage or send to server
    localStorage.setItem('user', JSON.stringify(user));
    alert('User information saved successfully!');
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col md={3}>
          <Nav className="flex-column">
            <Nav.Link active={view === 'profile'} onClick={() => setView('profile')}>Profile</Nav.Link>
            <Nav.Link active={view === 'appointments'} onClick={() => setView('appointments')}>Appointments</Nav.Link>
          </Nav>
        </Col>
        <Col md={9}>
          {view === 'profile' && (
            <div>
              <h2>General information</h2>
              <Form onSubmit={handleSubmit}>
                <Row className="mb-3">
                  <Form.Group as={Col} xs={12} md={6} controlId="formFirstName">
                    <Form.Label>First Name</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter your first name"
                      name="firstName"
                      value={user.username}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group as={Col} xs={12} md={6} controlId="formLastName">
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Also your last name"
                      name="lastName"
                      value={user.lastname}
                      onChange={handleChange}
                    />
                  </Form.Group>
                </Row>

                <Row className="mb-3">
                  <Form.Group as={Col} xs={12} md={6} controlId="formBirthday">
                    <Form.Label>Birthday</Form.Label>
                    <Form.Control
                      type="date"
                      name="birthday"
                      value={user.dateOfBirth}
                      onChange={handleDateChange}
                    />
                  </Form.Group>

                  <Form.Group as={Col} xs={12} md={6} controlId="formGender">
                    <Form.Label>Gender</Form.Label>
                    <Form.Control
                      as="select"
                      name="gender"
                      value={user.gender}
                      onChange={handleChange}
                    >
                      <option value="">Gender</option>
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                      <option value="Other">Other</option>
                    </Form.Control>
                  </Form.Group>
                </Row>

                <Row className="mb-3">
                  <Form.Group as={Col} xs={12} md={6} controlId="formEmail">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="name@company.com"
                      name="email"
                      value={user.email}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group as={Col} xs={12} md={6} controlId="formPhone">
                    <Form.Label>Phone</Form.Label>
                    <Form.Control
                      type="tel"
                      placeholder="+12-345 678 910"
                      name="phone"
                      value={user.phone}
                      onChange={handleChange}
                    />
                  </Form.Group>
                </Row>

                <Row className="mb-3">
                  <Form.Group as={Col} xs={12} md={6} controlId="formAddress">
                    <Form.Label>Address</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Enter your home address"
                      name="address"
                      value={user.address}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group as={Col} xs={12} md={6} controlId="formBloodType">
                    <Form.Label>Blood Type</Form.Label>
                    <Form.Control
                      as="select"
                      name="bloodType"
                      value={user.bloodType}
                      onChange={handleChange}
                    >
                      <option value="">Blood Type</option>
                      <option value="A+">A+</option>
                      <option value="A-">A-</option>
                      <option value="B+">B+</option>
                      <option value="B-">B-</option>
                      <option value="AB+">AB+</option>
                      <option value="AB-">AB-</option>
                      <option value="O+">O+</option>
                      <option value="O-">O-</option>
                    </Form.Control>
                  </Form.Group>
                </Row>

                <Button variant="primary" type="submit">
                  Save All
                </Button>
              </Form>
            </div>
          )}

          {view === 'appointments' && (
            <div>
              <h2>Appointments</h2>
              {appointments.length === 0 ? (
                <p>No appointments found.</p>
              ) : (
                <Row>
                  {appointments.map((appointment, index) => (
                    <Col md={6} key={index}>
                      <Card className="mb-3">
                        <Card.Body>
                          <Card.Title>{appointment.doctorid.username}</Card.Title>
                          <Card.Text>
                            <strong>Day:</strong> {appointment.day}<br />
                            <strong>Date:</strong> {new Date(appointment.date).toLocaleDateString()}<br />
                            <strong>User:</strong> {user.username}
                          </Card.Text>
                        </Card.Body>
                      </Card>
                    </Col>
                  ))}
                </Row>
              )}
            </div>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default UserInformation;
