import React, { useState, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

function AppointmentForm({ doctorId, onHide }) {
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [doctorinfo,setDoctorinfo] = useState([]);
  const [day,setDay]=useState('')
  const [end,setEnd]=useState('')

  useEffect(() => {
    // Fetch available appointments for the selected doctor
    axios.get(`http://localhost:8800/api/doctor/info/${doctorId}`)
    .then(response => {
        setDoctorinfo(response.data);
        setDay(response.data.startDay); // Update state here
        setEnd(response.data.endDay);   // Update state here
        setTime(`${response.data.startTime}-${response.data.endTime}`); // Update state here
      })
      .catch(error => console.error(error));
  }, [doctorId]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const userId = storedUser._id
    if (!userId) {
      console.error("User ID not found in local storage");
      return;
    }

    const newAppointment = {
      userid: userId,
      doctorid: doctorId,
      date: date,
      day: new Date(date).toLocaleString('en-US', { weekday: 'long' })
    };

    try {
      const response = await axios.post('http://localhost:8800/api/appointments', newAppointment);
      console.log(response.data);
      onHide();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="date">
        <Form.Label>Days</Form.Label>
        <Form.Control
          type="date"
          value={day}
          required
        />
      </Form.Group> 
      <Form.Group className="mb-3" controlId="time">
         <Form.Label>Available Time</Form.Label>
         <Form.Control
           type="text"
           value={time}
           readOnly
         />
        </Form.Group>

      <div className="text-center">
        <Button variant="outline-success" type="submit">Book Appointment</Button>
      </div>
    </Form>
  );
}

export default AppointmentForm;
