import React, { useState, useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';

const profile = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <Container className="mt-5">
      <Row className="justify-content-md-center">
        <Col md={6}>
          <Card>
            <Card.Header className="text-center">User Dashboard</Card.Header>
            <ListGroup variant="flush">
              <ListGroup.Item><strong>Username:</strong> {user.username}</ListGroup.Item>
              <ListGroup.Item><strong>Email:</strong> {user.email}</ListGroup.Item>
              <ListGroup.Item><strong>Date of Birth:</strong> {user.dateOfBirth}</ListGroup.Item>
              <ListGroup.Item><strong>Phone:</strong> {user.phone}</ListGroup.Item>
              <ListGroup.Item><strong>Address:</strong> {user.address}</ListGroup.Item>
              <ListGroup.Item><strong>Blood Type:</strong> {user.bloodType}</ListGroup.Item>
              <ListGroup.Item><strong>Medical History:</strong> {user.medicalHistory}</ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default profile;
