import React, { useState, useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Button from 'react-bootstrap/Button';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import "./navBar.css"; // Ensure this CSS file exists and is correctly applied
import Login from "./login.jsx"; // Adjust the path if necessary
import Register from "./Register.jsx";

function NavBar() {
  const [loginShow, setLoginShow] = useState(false);
  const [registerShow, setRegisterShow] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userType, setUserType] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Check if the user is already logged in
    const checkLoginStatus = async () => {
      try {
        const response = await axios.get('http://localhost:8800/api/auth/checkAuth');
        if (response.data.isLoggedIn) {
          setIsLoggedIn(true);
          setUserType(response.data.userType);
        }
      } catch (error) {
        console.error('Error checking login status', error);
      }
    };

    checkLoginStatus();
  }, []);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:8800/api/auth/logout');
       
      setIsLoggedIn(false);
      setUserType('');
      navigate('/');
    } catch (error) {
      console.error('Error during logout', error);
    }
  };

  const handleLoginSuccess = (userType) => {
    setIsLoggedIn(true);
    setUserType(userType);
    setLoginShow(false);
  };

  return (
    <>
      {['lg'].map((expand) => (
        <Navbar key={expand} expand={expand} className="bg-transparent mb-3 fixed-top">
          <Container fluid>
            <Navbar.Brand href="/">Hospital</Navbar.Brand>
            <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-${expand}`} />
            <Navbar.Offcanvas
              id={`offcanvasNavbar-expand-${expand}`}
              aria-labelledby={`offcanvasNavbarLabel-expand-${expand}`}
              placement="end"
            >
              <Offcanvas.Header closeButton>
                <Offcanvas.Title id={`offcanvasNavbarLabel-expand-${expand}`}>
                  Hospital
                </Offcanvas.Title>
              </Offcanvas.Header>
              <Offcanvas.Body>
                <Nav className="justify-content-end flex-grow-1 pe-3 d-flex">
                  <Nav.Link href="/">Home</Nav.Link>
                  <Nav.Link href="Appointment">Appointment</Nav.Link>
                  <Nav.Link href="abort">Abort</Nav.Link>
                  {isLoggedIn ? (
                    <>
                      <Nav.Link href={userType === 'admin' ? '/admin' : userType === 'doctor' ? '/doctor' : '/dashboard'}>
                        Dashboard
                      </Nav.Link>
                      <Button variant="outline-danger" onClick={handleLogout}>Logout</Button>
                    </>
                  ) : (
                    <>
                      <Button variant="outline-success" onClick={() => setLoginShow(true)}>Login</Button>
                      <Button variant="outline-success" onClick={() => setRegisterShow(true)}>Sign Up</Button>
                    </>
                  )}
                </Nav>
              </Offcanvas.Body>
            </Navbar.Offcanvas>
          </Container>
        </Navbar>
      ))}
      <Login
        show={loginShow}
        onHide={() => setLoginShow(false)}
        onLoginSuccess={handleLoginSuccess}
      />
      <Register
        show={registerShow}
        onHide={() => setRegisterShow(false)}
      />
    </>
  );
}

export default NavBar;
